const readline = require('readline').createInterface({
  input: process.stdin,
  output: process.stdout
});

readline.question('Enter a number: ', num => {
  const number = parseInt(num);
  if (number % 2 === 0) {
    console.log('Even');
  } else {
    console.log('Odd');
  }
  readline.close();
});
