const fs = require('fs');
const csv = require('csv-parser');
const filename = process.argv[2] || 'data.csv';

let rowCount = 0;
let colCount = 0;

fs.createReadStream(filename)
  .pipe(csv())
  .on('data', (row) => {
    rowCount++;
    if (rowCount === 1) {
      colCount = Object.keys(row).length;
    }
  })
  .on('end', () => {
    console.log(`Rows: ${rowCount}, Columns: ${colCount}`);
  });
